        private static void ValidationForAddDeleteTags(string templateId, List<string> templateLabels, List<string> documentLabels)
        {
            // verify the required parameter 'documentId' is not null and in Guid format.
            if (string.IsNullOrEmpty(templateId) && Guid.TryParse(templateId.ToString(), out _))
            {
                throw new ApiException(400, ApiValidationMessages.TemplateIdRequired);
            }

            // verify the required parameter 'labels' is not Empty
            if (templateLabels.Any(x => string.IsNullOrEmpty(x)) || documentLabels.Any(x => string.IsNullOrEmpty(x)))
            {
                throw new ApiException(400, ApiValidationMessages.EmptyLabels);
            }

            // verify the required parameter 'labels' is not having more than 255 characters
            if (templateLabels.Any(x => x.Length > 255) || documentLabels.Any(x => x.Length > 255))
            {
                throw new ApiException(400, ApiValidationMessages.LabelMaxLength);
            }

            // verify the required parameter 'labels' is not contains more than 50 tags.
            if (templateLabels.Count > 50 || documentLabels.Count > 50)
            {
                throw new ApiException(400, ApiValidationMessages.MaxTemplateOrDocumentLabelsCount);
            }
        }

